import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import ClimbingBoxLoader from "react-spinners/ClimbingBoxLoader";
import "./All.css"


const GameList = () => {

    let { storeId, storeName, gameId } = useParams();
    const [game, setGame] = useState(null)

    useEffect(() => {
        const api_link = `https://www.cheapshark.com/api/1.0/games?id=${gameId}`
        fetch(api_link)
            .then(g => g.json())
            .then(g => {
                setGame(g)
            })
    }, [gameId])

    if (game === null)
        return (
            <div>
                <ClimbingBoxLoader className="orta">

                </ClimbingBoxLoader>
            </div>
        )

    return (
        <div className="container text-center">
            <h5>{game.info.title}</h5>
            <img src={game.info.thumb} /><br /><br />
            <h4>En ucuz Fiyat:${game.cheapestPriceEver.price}</h4>
            <Link to={`/${storeId}/${storeName}/Deal`}><button className="btn btn-outline-dark">Geri</button></Link>
            <h4>Bugune Kadarki En Büyük Fırsatlar</h4>
            {
                game.deals.map(d =>
                    <div>
                        <div class="card-group col-8 row row-cols-2 row-cols-md-1 g-4 ">
                            <div class="card">
                                <div class="card-body">
                                    <h5 class="card-title">Satış Fiyatı: ${d.price}</h5>
                                    <p class="card-text">İndirimsiz Fiyatı: ${d.retailPrice}</p>
                                    <p class="card-text">İndirim Oranı:% {d.savings}</p>

                                </div>

                            </div>
                        </div>
                    </div>
                )
            }
        </div>
    )
}

export default GameList;

