import axios from 'axios'
import React, { useEffect, useState } from 'react'


 const Home = () => {


  const [api,setApi]=useState([])


  useEffect(()=>{
    const api_link="https://restcountries.com/v3.1/all"
    axios.get(api_link)
    .then(response=>{
      setApi(response.data)
    })
  },[])


  console.log(api,"apii")

  const [text,setText]=useState("")
  const [mejas,setMejas]=useState([])

  const onChangeFunc =(e)=>{
    setText(e.target.value)
  }

  const mejasfunc =()=>{
   setMejas(prevent=> [...prevent,text])
   setText("")
  }

  console.log(mejas)

  return (
   <>
   <h4 style={{textAlign:"center"}}>Todo Ekle Api</h4>
   <hr/>
   <input value={text} onChange={onChangeFunc} style={{marginLeft:570,width:500,height:50,outline:"none"}} type='text' placeholder='Ekle' />
   <button onClick={mejasfunc} style={{background:"black",color:"white",width:100,height:60,cursor:"pointer",margin:5}} className='btn btn-dark'>Kaydet</button>
   <h4 style={{textAlign:"center"}}>GÃ¶ster</h4>
   {
    mejas.map((tod,i)=>
    <li style={{marginLeft:575,listStyleType:"none",borderBottom:"1px solid black",width:350}} key={i}><br/>
      {tod}
      <button style={{background:"red",border:"1px solid white",color:"white",width:20,height:20,textAlign:"center"}}>X</button>
    </li>
    )
   }


   <div>
    {

      api.map((ulke,i)=>
      <ol key={i}>
        {ulke.area}
      </ol>
      )
    }
   </div>
   </>
  )
}

export default Home;
